/**
 * Device Checker module to determine if the device used is mobile, iPad or iPhone.
 */

window.device = (function () {
    var isMobile, isiPad, isiPhone, isiPod, isiOS, isAndroid, desktop;

    //set variable right away once when the module is loaded at the first time
    (function() {
        var userAgent = navigator.userAgent;

        isMobile = /Android|webOS|iPhone|iPod|BlackBerry/i.test(userAgent);
        isiPad = /iPad/i.test(userAgent);
        isAndroid = /Android/i.test(userAgent);
        isiOS = /iPhone|iPad|iPod/i.test(userAgent);
        isiPhone = /iPhone/i.test(userAgent);
        isiPod = /iPod/i.test(userAgent);
    })();

    desktop = !(isMobile || isiPad || isiPhone || isiPod);

    return {
        isMobile: isMobile,
        isiPad: isiPad,
        isiPhone: isiPhone,
        isAndroid: isAndroid,
        isiOS: isiOS,
        isiPod: isiPod,
        desktop: desktop
    };
}());
